INSERT INTO `sabit_sayfalar` (`sayfa_id`, `sayfa_adi`, `sayfa_aciklama`, `sayfa_tarih`) VALUES
(1, 'hakkımda', '<p style=\"text-align:justify\"><span style=\"font-size:24px\"><span style=\"color:#00b6e7\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;<em>Merhaba,</em></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:Poppins\">Ben Metin OKUR, 21 yaşındayım. Bilecik Şeyh Edebali &Uuml;niversitesi, Bilgisayar M&uuml;hendisliği B&ouml;l&uuml;m&uuml; 3. sınıf &ouml;ğrencisiyim. Programlama dilleri, bu programlama dillerinin kullanım alanları, mantığı ve işlevleri gibi alanlarda ilgiliyim. İlgili olmam hasebiyle bir&ccedil;ok programlama dili &ouml;ğrendim ve &ouml;ğrenmeyede devam ediyorum. Yaklaşık 1 senedir web programlama alanında kendimi geliştirmeye &ccedil;alışıyorum. Farklı farklı tasarımlar yaparak kendimi bu alanda daha nasıl geliştirebilirim mantığı &ccedil;er&ccedil;evesinde adımlar atıyorum.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:24px\"><span style=\"color:#00b6e7\"><span style=\"font-family:Calibri,sans-serif\"><em>Siteyi Kurma&nbsp;Amacım</em></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Poppins\">Benim gibi &ouml;ğrenci olup yazılım alanında ilerleyen arkadaşlarıma veya yazılım alanında ilgili olanlara &ouml;ğrendiğim bilgileri paylaşmak ve paylaşırken bilgilerimi taze tutmak amacıyla Metin okur adlı blog sitemi a&ccedil;maya karar verdim.&nbsp;&nbsp;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:24px\"><span style=\"color:#00b6e7\"><span style=\"font-family:Calibri,sans-serif\"><em>Hedeflerim ve&nbsp;Projelerim&nbsp;&nbsp;</em></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">&nbsp;<span style=\"font-family:Poppins\">T&uuml;rkiye&nbsp; &ccedil;apında markalaşmış firmaların dijitalde kendimi en iyi şekilde yansıtacak tasarımlar yapmayı ve yapılan &ccedil;alışmalara katılmayı hedefliyorum. Şuan hali hazırda bulunan web sitemi &ouml;ğrendiğim bilgileri entegre ederek zenginleştirmek istiyorum. Sadece blog siteleri değil de firma &uuml;zerine entegre olmuş siteler yapmak projelerim arasındadır.</span></span></p>\r\n', '2021-09-10 19:17:33');

